#!/usr/bin/env python3
import re

def wrap_dna(dna, width):
	nt_list = []
	new = ''
	dna = re.sub(r'\n', '', dna)
	width = int(width)
	for nt in dna:
		nt_list.append(nt)
	for index in nt_list:
		new += "".join(nt_list[:width])+'\n'
		del nt_list[:width]
	return new 

