#!/usr/bin/env python3

#string to evaluate
dna = ''

A_count = dna.count('A') 			#count number of As in string
T_count = dna.count('T')			#count number of Ts in string

length = len(dna)							#determine length of string

AT_content = ((int(A_count) + int(T_count)) / length) * 100		#add count of As and count of Ts, divide by length, and multiply by 100 to get percentage of AT content

print(AT_content)
