#!/usr/bin/env python3

#string to evaluate
dna_mixed = ''

dna_upper = dna_mixed.upper()			#make uppercase, to avoid issues with mixed case string

A_count = dna_upper.count('A')		#count
T_count = dna_upper.count('T')
C_count = dna_upper.count('C')
G_count = dna_upper.count('G')

output_string = "This sequences contains {} As, {} Ts, {} Cs, and {} Gs."		#string for printing with string.format

print(output_string.format(A_count, T_count, C_count, G_count))
