#!/usr/bin/env python3

dna_mixed = ''

dna_upper = dna_mixed.upper()

A_count = dna_upper.count('A')
T_count = dna_upper.count('T')
C_count = dna_upper.count('C')
G_count = dna_upper.count('G')

output_string = "This sequences contains {} As, {} Ts, {} Cs, and {} Gs."

print(output_string.format(A_count, T_count, C_count, G_count))
