#!/usr/bin/env python3

#string to evaluate
dna = ''

EcoRI = 'GAATTC'  	#pattern to find

EcoRI_begin = dna.find(EcoRI)		#assign output of dna.find to variable

StartPos = int(EcoRI_begin) + 1		#add 1 to dna.find output to switch from python counting to actual position in sequence
EndPos = int(StartPos) + len(EcoRI)

string = 'EcoRI startPos:{} endPos:{}'
print(string.format(StartPos, EndPos))
